// src/services.js
import axios from "axios";

const BASE_URL = process.env.REACT_APP_API_BASE_URL || "https://239a-2804-14c-9d83-83e7-00-10b4.ngrok-free.app";

// Função para iniciar o chat
export const startChat = async (chatId) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/start_chat`, { chat_id: chatId });
    return response.data;
  } catch (error) {
    console.error("Erro ao iniciar o chat", error);
    throw new Error("Não foi possível iniciar o chat. Tente novamente mais tarde.");
  }
};

// Função para salvar o fluxo do chat
export const saveFlow = async (flowData) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/save_flow`, { flow_data: flowData });
    return response.data;
  } catch (error) {
    console.error("Erro ao salvar fluxo", error);
    throw new Error("Não foi possível salvar o fluxo. Tente novamente mais tarde.");
  }
};
