// src/ChatComponent.js
import React, { useState } from "react";
import { sendMessage } from "./services"; // Função para enviar a mensagem ao backend

const ChatComponent = () => {
  const [userInput, setUserInput] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const handleMessage = async () => {
    // Enviar mensagem para o backend
    const response = await sendMessage(userInput);
    setChatHistory([...chatHistory, { from: "user", message: userInput }]);
    setChatHistory([...chatHistory, { from: "bot", message: response }]);
    setUserInput("");
  };

  return (
    <div>
      <div>
        {chatHistory.map((entry, index) => (
          <div key={index}>
            <strong>{entry.from}: </strong>{entry.message}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />
      <button onClick={handleMessage}>Enviar</button>
    </div>
  );
};

export default ChatComponent;
