import React, { useState, useCallback } from 'react';
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  Handle,
  useNodesState,
  useEdgesState
} from 'react-flow-renderer';
import { saveFlow } from './services';

const nodeStyles = {
  base: {
    padding: '20px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    border: '2px solid #2684ff',
    minWidth: '300px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
  },
  input: {
    margin: '10px 0',
    padding: '8px',
    width: '100%',
    borderRadius: '4px',
    border: '1px solid #ddd'
  },
  section: {
    margin: '15px 0',
    padding: '10px',
    backgroundColor: '#f8f9fa',
    borderRadius: '6px'
  },
  select: {
    width: '100%',
    padding: '8px',
    margin: '5px 0'
  }
};

// Componentes dos NÃ³s
const MessageNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>ð¬ Mensagem</h4>
    <textarea
      style={{ ...nodeStyles.input, height: '100px' }}
      value={data.content}
      onChange={(e) => data.updateContent(e.target.value)}
      placeholder="Escreva sua mensagem..."
    />
    <Handle type="source" position="bottom" />
  </div>
);

const QuestionNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>â Pergunta</h4>
    <input
      type="text"
      style={nodeStyles.input}
      value={data.question}
      onChange={(e) => data.updateQuestion(e.target.value)}
      placeholder="Digite a pergunta"
    />
    <div style={nodeStyles.section}>
      <h5>OpÃ§Ãµes de Resposta:</h5>
      {data.options.map((opt, index) => (
        <div key={index} style={{ margin: '5px 0' }}>
          <input
            type="text"
            value={opt}
            style={{ ...nodeStyles.input, width: '80%' }}
            onChange={(e) => data.updateOption(index, e.target.value)}
          />
          <Handle
            type="source"
            position="bottom"
            id={`option-${index}`}
            style={{ left: '85%' }}
          />
        </div>
      ))}
      <button onClick={data.addOption}>+ Adicionar OpÃ§Ã£o</button>
    </div>
  </div>
);

const MediaNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>ð¼ï¸ MÃ­dia</h4>
    <input
      type="file"
      onChange={(e) => data.handleFileUpload(e.target.files[0])}
      style={nodeStyles.input}
    />
    <div style={nodeStyles.section}>
      <h5>Ou insira URL:</h5>
      <input
        type="text"
        value={data.url}
        onChange={(e) => data.updateUrl(e.target.value)}
        placeholder="https://exemplo.com/imagem.jpg"
        style={nodeStyles.input}
      />
    </div>
    <Handle type="source" position="bottom" />
  </div>
);

const ConditionNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>âï¸ CondiÃ§Ã£o</h4>
    <select
      value={data.conditionType}
      onChange={(e) => data.updateConditionType(e.target.value)}
      style={nodeStyles.select}
    >
      <option value="cadastro">Verificar Cadastro</option>
      <option value="valor">Comparar Valores</option>
    </select>

    {data.conditionType === 'cadastro' && (
      <div style={nodeStyles.section}>
        <input
          type="text"
          value={data.field}
          onChange={(e) => data.updateField(e.target.value)}
          placeholder="Campo para verificaÃ§Ã£o (ex: PHONE)"
          style={nodeStyles.input}
        />
      </div>
    )}

    {data.conditionType === 'valor' && (
      <div style={nodeStyles.section}>
        <input
          type="text"
          value={data.comparison}
          onChange={(e) => data.updateComparison(e.target.value)}
          placeholder="ExpressÃ£o (ex: VALUE > 100)"
          style={nodeStyles.input}
        />
      </div>
    )}
    
    <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '15px' }}>
      <Handle type="source" position="bottom" id="true" style={{ left: '30%' }} />
      <Handle type="source" position="bottom" id="false" style={{ left: '70%' }} />
    </div>
  </div>
);

const IntervalNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>â±ï¸ Intervalo</h4>
    <div style={nodeStyles.section}>
      <input
        type="number"
        value={data.duration}
        onChange={(e) => data.updateDuration(e.target.value)}
        placeholder="DuraÃ§Ã£o em segundos"
        min="1"
        max="7200"
        style={nodeStyles.input}
      />
      <small>MÃ¡ximo: 2 horas (7200 segundos)</small>
    </div>
    <Handle type="source" position="bottom" />
  </div>
);

const ChatGPTNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>ð¤ Chat GPT</h4>
    <input
      type="password"
      value={data.apiKey}
      onChange={(e) => data.updateApiKey(e.target.value)}
      placeholder="Insira sua API Key da OpenAI"
      style={nodeStyles.input}
    />
    <textarea
      value={data.instructions}
      onChange={(e) => data.updateInstructions(e.target.value)}
      placeholder="InstruÃ§Ãµes para o assistente..."
      style={{ ...nodeStyles.input, height: '100px' }}
    />
    <Handle type="source" position="bottom" />
  </div>
);

const APINode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>ð API Externa</h4>
    <div style={nodeStyles.section}>
      <select 
        value={data.method} 
        onChange={(e) => data.updateMethod(e.target.value)}
        style={nodeStyles.select}
      >
        <option value="GET">GET</option>
        <option value="POST">POST</option>
        <option value="PUT">PUT</option>
        <option value="DELETE">DELETE</option>
      </select>
      <input
        type="text"
        value={data.url}
        onChange={(e) => data.updateUrl(e.target.value)}
        placeholder="URL da API"
        style={nodeStyles.input}
      />
    </div>
    
    <div style={nodeStyles.section}>
      <h5>CabeÃ§alhos:</h5>
      <textarea
        value={data.headers}
        onChange={(e) => data.updateHeaders(e.target.value)}
        placeholder="JSON de cabeÃ§alhos"
        style={{ ...nodeStyles.input, height: '80px' }}
      />
    </div>
    
    <div style={nodeStyles.section}>
      <h5>Corpo da RequisiÃ§Ã£o:</h5>
      <textarea
        value={data.body}
        onChange={(e) => data.updateBody(e.target.value)}
        placeholder="JSON do corpo"
        style={{ ...nodeStyles.input, height: '100px' }}
      />
    </div>
    <Handle type="source" position="bottom" />
  </div>
);

const UpdateCRMNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>ð Atualizar CRM</h4>
    <div style={nodeStyles.section}>
      <select
        value={data.entity}
        onChange={(e) => data.updateEntity(e.target.value)}
        style={nodeStyles.select}
      >
        <option value="lead">Lead</option>
        <option value="deal">NegÃ³cio</option>
        <option value="contact">Contato</option>
        <option value="company">Empresa</option>
      </select>
      
      <input
        type="text"
        value={data.field}
        onChange={(e) => data.updateField(e.target.value)}
        placeholder="Campo para atualizar"
        style={nodeStyles.input}
      />
      
      <input
        type="text"
        value={data.value}
        onChange={(e) => data.updateValue(e.target.value)}
        placeholder="Novo valor"
        style={nodeStyles.input}
      />
    </div>
    <Handle type="source" position="bottom" />
  </div>
);

const CreateRecordNode = ({ data }) => (
  <div style={nodeStyles.base}>
    <Handle type="target" position="top" />
    <h4>ð Criar Cadastro</h4>
    <div style={nodeStyles.section}>
      <select
        value={data.entity}
        onChange={(e) => data.updateEntity(e.target.value)}
        style={nodeStyles.select}
      >
        <option value="lead">Lead</option>
        <option value="deal">NegÃ³cio</option>
        <option value="contact">Contato</option>
        <option value="company">Empresa</option>
      </select>

      <select
        value={data.stage}
        onChange={(e) => data.updateStage(e.target.value)}
        style={nodeStyles.select}
      >
        <option value="new">Novo</option>
        <option value="qualified">Qualificado</option>
        <option value="proposal">Proposta</option>
        <option value="closed">Fechado</option>
      </select>

      <textarea
        value={data.fields}
        onChange={(e) => data.updateFields(e.target.value)}
        placeholder="Campos (JSON)"
        style={{ ...nodeStyles.input, height: '150px' }}
      />
    </div>
    <Handle type="source" position="bottom" />
  </div>
);

const FlowEditor = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [idCounter, setIdCounter] = useState(0);

  const createNode = (type, initialData) => {
    const nodeId = `${type}-${idCounter}`;
    setIdCounter(idCounter + 1);
    
    const updateFunctions = {
      message: {
        updateContent: (content) => updateNode(nodeId, { content })
      },
      question: {
        updateQuestion: (question) => updateNode(nodeId, { question }),
        addOption: () => updateNode(nodeId, {
          options: [...initialData.options, `OpÃ§Ã£o ${initialData.options.length + 1}`]
        }),
        updateOption: (index, value) => {
          const newOptions = [...initialData.options];
          newOptions[index] = value;
          updateNode(nodeId, { options: newOptions });
        }
      },
      media: {
        handleFileUpload: (file) => updateNode(nodeId, { file }),
        updateUrl: (url) => updateNode(nodeId, { url })
      },
      condition: {
        updateConditionType: (conditionType) => updateNode(nodeId, { conditionType }),
        updateField: (field) => updateNode(nodeId, { field }),
        updateComparison: (comparison) => updateNode(nodeId, { comparison })
      },
      interval: {
        updateDuration: (duration) => updateNode(nodeId, { duration })
      },
      chatgpt: {
        updateApiKey: (apiKey) => updateNode(nodeId, { apiKey }),
        updateInstructions: (instructions) => updateNode(nodeId, { instructions })
      },
      api: {
        updateMethod: (method) => updateNode(nodeId, { method }),
        updateUrl: (url) => updateNode(nodeId, { url }),
        updateHeaders: (headers) => updateNode(nodeId, { headers }),
        updateBody: (body) => updateNode(nodeId, { body })
      },
      updateCRM: {
        updateEntity: (entity) => updateNode(nodeId, { entity }),
        updateField: (field) => updateNode(nodeId, { field }),
        updateValue: (value) => updateNode(nodeId, { value })
      },
      createRecord: {
        updateEntity: (entity) => updateNode(nodeId, { entity }),
        updateStage: (stage) => updateNode(nodeId, { stage }),
        updateFields: (fields) => updateNode(nodeId, { fields })
      }
    };

    return {
      id: nodeId,
      type,
      position: { x: Math.random() * 400, y: Math.random() * 400 },
      draggable: true,
      data: {
        ...initialData,
        ...updateFunctions[type]
      }
    };
  };

  const updateNode = (nodeId, newData) => {
    setNodes(nds => nds.map(node => 
      node.id === nodeId ? { ...node, data: { ...node.data, ...newData } } : node
    ));
  };

  const addNode = (type) => {
    const nodeConfig = {
      message: { content: '' },
      question: { question: '', options: ['Sim', 'NÃ£o'] },
      media: { url: '', file: null },
      condition: { conditionType: 'cadastro', field: '', comparison: '' },
      interval: { duration: 300 },
      chatgpt: { apiKey: '', instructions: '' },
      api: { method: 'GET', url: '', headers: '', body: '' },
      updateCRM: { entity: 'lead', field: '', value: '' },
      createRecord: { entity: 'lead', stage: 'new', fields: '{}' }
    };

    setNodes(nds => [...nds, createNode(type, nodeConfig[type])]);
  };

  return (
    <div style={{ height: '100vh', display: 'flex' }}>
      <div style={{
        width: '250px',
        padding: '20px',
        backgroundColor: '#f8f9fa',
        borderRight: '1px solid #ddd',
        overflowY: 'auto'
      }}>
        <h3>Ferramentas</h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <button onClick={() => addNode('message')}>Nova Mensagem</button>
          <button onClick={() => addNode('question')}>Nova Pergunta</button>
          <button onClick={() => addNode('media')}>Nova MÃ­dia</button>
