// src/FlowEditor.js
import React, { useState } from "react";
import ReactFlow, { addEdge, MiniMap, Controls } from "react-flow-renderer"; // Corrigido a importação
import { saveFlow } from "./services"; // Para salvar o fluxo

// Componente FlowEditor
const FlowEditor = ({ elements, setElements }) => {
  // Função para adicionar um nó de mensagem
  const addMessageNode = () => {
    const newNode = {
      id: `node-${elements.length + 1}`, // Garante que o id do nó seja único
      type: "message", // Tipo de nó
      data: { label: `Mensagem ${elements.length + 1}` }, // Rótulo do nó
      position: { x: 100, y: 100 + elements.length * 100 }, // Posição do nó
      style: { border: "1px solid #ddd", padding: "10px" }, // Estilo opcional
    };
    // Atualizando o estado com o novo nó
    setElements((els) => [...els, newNode]);
  };

  // Função para salvar o fluxo
  const handleSaveFlow = async () => {
    const flowData = { elements }; // Aqui você pode enviar os dados para o backend
    await saveFlow(flowData);
    alert("Fluxo salvo com sucesso!");
  };

  // Lógica para conectar os nós
  const onConnect = (params) => setElements((els) => addEdge(params, els));

  return (
    <div style={{ height: "100vh" }}>
      <div style={{ position: "absolute", top: 20, left: 20 }}>
        <button onClick={addMessageNode}>Adicionar Mensagem</button>
        <button onClick={handleSaveFlow}>Salvar Fluxo</button>
      </div>
      <ReactFlow
        elements={elements} // Passando os elementos para o ReactFlow
        onConnect={onConnect} // Definindo o comportamento de conexão entre nós
        style={{ height: "100%" }} // Ajustando a altura do fluxo
      >
        <MiniMap />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default FlowEditor;
