// src/App.js
import React, { useState, useEffect } from "react";
import ReactFlow, { addEdge, MiniMap, Controls, ReactFlowProvider } from "react-flow-renderer";
import FlowEditor from "./FlowEditor"; // Importe o componente FlowEditor

function App() {
  const [elements, setElements] = useState([]);

  // Inicializa os elementos com um nó padrão
  useEffect(() => {
    const initialElements = [
      { id: "1", type: "message", data: { label: "Escolha o setor" }, position: { x: 100, y: 100 } },
      { id: "2", type: "message", data: { label: "Mensagem de resposta" }, position: { x: 300, y: 100 } },
      { id: "e1-2", source: "1", target: "2", animated: true },  // Conectando os dois nós
    ];
    setElements(initialElements); // Atualiza o estado com os elementos iniciais
  }, []);

  return (
    <div style={{ height: "100vh" }}>
      <ReactFlowProvider>
        <FlowEditor elements={elements} setElements={setElements} /> {/* Usando o FlowEditor */}
      </ReactFlowProvider>
    </div>
  );
}

export default App;
