// src/App.js
import React, { useState } from "react";
import StartChat from "./StartChat";
import InteractionOptions from "./InteractionOptions";

function App() {
  const [chatStarted, setChatStarted] = useState(false);
  const [selectedInteraction, setSelectedInteraction] = useState(null);

  const handleInteractionSelect = (interaction) => {
    setSelectedInteraction(interaction);
    console.log("Interação selecionada:", interaction);
    // Aqui você pode adicionar a lógica para cada interação
  };

  return (
    <div style={{ height: "100vh", padding: "20px" }}>
      {!chatStarted ? (
        <StartChat onStart={() => setChatStarted(true)} />
      ) : (
        <InteractionOptions onSelect={handleInteractionSelect} />
      )}

      {selectedInteraction && (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <h3>Interação selecionada: {selectedInteraction}</h3>
        </div>
      )}
    </div>
  );
}

export default App;
