import React, { useState, useCallback, useEffect } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    Handle,
    useNodesState,
    useEdgesState,
    addEdge
} from 'reactflow';
import 'reactflow/dist/style.css';

// Estilos atualizados
const flowContainerStyles = {
    width: '100%',
    height: 'calc(100vh - 20px)',
    position: 'relative',
    overflow: 'hidden'
};

const nodeStyles = {
    base: {
        padding: '20px',
        backgroundColor: '#fff',
        borderRadius: '8px',
        border: '2px solid #2684ff',
        minWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    input: {
        margin: '10px 0',
        padding: '8px',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd'
    },
    section: {
        margin: '15px 0',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '6px',
        width: '100%'
    },
    select: {
        width: '100%',
        padding: '8px',
        margin: '5px 0'
    },
    deleteButton: {
        background: 'red',
        color: 'white',
        border: 'none',
        borderRadius: '50%',
        width: '24px',
        height: '24px',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: '10px'
    },
    menuButton: {
        padding: '10px 15px',
        fontSize: '16px',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        margin: '5px'
    },
    menuContainer: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '10px',
        padding: '15px'
    }
};

const handleStyle = {
    width: '15px',
    height: '15px',
    backgroundColor: '#2684ff',
    border: '2px solid #fff',
    borderRadius: '50%'
};

// Componente do Menu de Adi√ß√£o de N√≥s
const AddNodeMenu = React.memo(({ id, data }) => (
    <div style={{ ...nodeStyles.base, minWidth: '400px' }}>
