// src/services.js
export const saveFlow = async (flowData) => {
  try {
    const response = await fetch(" https://239a-2804-14c-9d83-83e7-00-10b4.ngrok-free.app/api/saveFlow", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(flowData),
    });
    if (!response.ok) {
      throw new Error("Falha ao salvar o fluxo");
    }
    return await response.json();
  } catch (error) {
    console.error("Erro ao salvar fluxo:", error);
  }
};
