// src/FlowEditor.js
import React, { useState, useCallback } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  Handle,
} from "react-flow-renderer";
import { saveFlow } from "./services";

// Estilos dos componentes
const styles = {
  sidebar: {
    width: "250px",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  sidebarButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  saveButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "20px 0",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  node: {
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "5px",
    border: "2px solid #007bff",
    textAlign: "center",
    minWidth: "150px",
  },
};

// Componentes personalizados dos nós
const nodeTypes = {
  message: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  question: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  media: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  condition: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  transfer: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  interval: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  chatgpt: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  api: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  update_crm: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
  create_record: ({ data }) => (
    <div style={styles.node}>
      <Handle type="target" position="top" />
      <strong>{data.label}</strong>
      <Handle type="source" position="bottom" />
    </div>
  ),
};

const FlowEditor = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [idCounter, setIdCounter] = useState(0);

  // Função para criar conexões
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // Função para adicionar novos nós
  const addNode = (type, label) => {
    const newNode = {
      id: `${type}-${idCounter}`,
      type,
      data: { label },
      position: { 
        x: Math.random() * window.innerWidth * 0.6,
        y: Math.random() * window.innerHeight * 0.8
      },
    };
    setIdCounter(idCounter + 1);
    setNodes((nds) => [...nds, newNode]);
  };

  // Função para salvar o fluxo
  const handleSaveFlow = async () => {
    try {
      await saveFlow({ nodes, edges });
      alert("Fluxo salvo com sucesso!");
    } catch (error) {
      alert("Erro ao salvar o fluxo!");
    }
  };

  return (
    <div style={{ height: "100vh", display: "flex" }}>
      {/* Painel lateral */}
      <div style={styles.sidebar}>
        <h3>Interações</h3>
        {Object.entries({
          message: "Mensagem",
          question: "Pergunta",
          media: "Mídia",
          condition: "Condição",
          transfer: "Transferir",
          interval: "Intervalo",
          chatgpt: "Chat GPT",
          api: "API Externa",
          update_crm: "Atualizar CRM",
          create_record: "Criar Cadastro",
        }).map(([type, label]) => (
          <button
            key={type}
            onClick={() => addNode(type, label)}
            style={styles.sidebarButton}
          >
            {label}
          </button>
        ))}
        <button onClick={handleSaveFlow} style={styles.saveButton}>
          Salvar Fluxo
        </button>
      </div>

      {/* Área de trabalho */}
      <div style={{ flex: 1, backgroundColor: "#f0f2f5" }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onConnect={onConnect}
          onNodesChange={(changes) => {
            setNodes((nds) =>
              nds.map((node) => {
                const change = changes.find((c) => c.id === node.id);
                return change?.position ? { ...node, position: change.position } : node;
              })
            );
          }}
          nodeTypes={nodeTypes}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background variant="dots" gap={12} size={1} />
        </ReactFlow>
      </div>
    </div>
  );
};

export default FlowEditor;
