// src/InteractionOptions.js
import React from "react";

const InteractionOptions = ({ onSelect }) => {
  return (
    <div style={styles.container}>
      <h2>Qual a interação que deseja?</h2>

      <div style={styles.section}>
        <h3>Conversa</h3>
        <button onClick={() => onSelect("Mensagem")}>Mensagem</button>
        <button onClick={() => onSelect("Pergunta")}>Pergunta</button>
        <button onClick={() => onSelect("Mídia")}>Mídia</button>
      </div>

      <div style={styles.section}>
        <h3>Ações do Chat</h3>
        <button onClick={() => onSelect("Condição")}>Condição</button>
        <button onClick={() => onSelect("Transferir")}>Transferir</button>
        <button onClick={() => onSelect("Horário")}>Horário</button>
        <button onClick={() => onSelect("Finalizar")}>Finalizar</button>
        <button onClick={() => onSelect("Intervalo")}>Intervalo</button>
      </div>

      <div style={styles.section}>
        <h3>Integrações</h3>
        <button onClick={() => onSelect("Chat GPT")}>Chat GPT</button>
        <button onClick={() => onSelect("API Externa")}>API Externa</button>
      </div>

      <div style={styles.section}>
        <h3>Bitrix</h3>
        <button onClick={() => onSelect("Atualizar CRM")}>Atualizar CRM</button>
        <button onClick={() => onSelect("Criar Cadastro")}>Criar Cadastro</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    backgroundColor: "#f9f9f9",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    maxWidth: "600px",
    margin: "20px auto",
  },
  section: {
    marginBottom: "20px",
  },
  button: {
    margin: "5px",
    padding: "10px 15px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default InteractionOptions;
