import React, { useState, useCallback } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    Handle,
    useNodesState,
    useEdgesState,
    addEdge
} from 'react-flow-renderer';

// Estilos atualizados
const flowContainerStyles = {
    width: '100%',
    height: 'calc(100vh - 60px)',
    position: 'relative',
    overflow: 'hidden'
};

const nodeStyles = {
    base: {
        padding: '20px',
        backgroundColor: '#fff',
        borderRadius: '8px',
        border: '2px solid #2684ff',
        minWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    input: {
        margin: '10px 0',
        padding: '8px',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd'
    },
    section: {
        margin: '15px 0',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '6px',
        width: '100%'
    },
    select: {
        width: '100%',
        padding: '8px',
        margin: '5px 0'
    },
    deleteButton: {
        background: 'red',
        color: 'white',
        border: 'none',
        borderRadius: '50%',
        width: '24px',
        height: '24px',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: '10px'
    }
};

// Estilo personalizado para os Handles (bolinhas de conex√£o)
const handleStyle = {
    width: '15px',
    height: '15px',
    backgroundColor: '#2684ff',
    border: '2px solid #fff',
    borderRadius: '50%'
};

// Novo estilo para os bot√µes da barra superior
const topBarButtonStyle = {
    padding: '10px 15px',
    fontSize: '16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    margin: '5px'
};

// Componente de n√≥ pr√©-configurado
const PreConfiguredNode = React.memo(({ id, data }) => {
    return (
        <div style={nodeStyles.base}>
            <Handle type="target" position="left" style={handleStyle} />
            <h4>‚öôÔ∏è N√≥ Pr√©-configurado</h4>
            <div style={nodeStyles.section}>
                <p>Este n√≥ j√° vem com configura√ß√µes prontas.</p>
                <ul>
                    <li>Op√ß√£o 1: Ativada</li>
                    <li>Op√ß√£o 2: Desativada</li>
                    <li>Op√ß√£o 3: Personaliz√°vel</li>
                </ul>
            </div>
            <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>üóëÔ∏è</button>
            <Handle type="source" position="right" style={handleStyle} />
        </div>
    );
});

// Defina nodeTypes fora do componente FlowEditor
const nodeTypes = {
    message: MessageNode,
    question: QuestionNode,
    media: MediaNode,
    condition: ConditionNode,
    interval: IntervalNode,
    chatgpt: ChatGPTNode,
    api: APINode,
    updateCRM: UpdateCRMNode,
    createRecord: CreateRecordNode,
    preConfigured: PreConfiguredNode
};

const FlowEditor = () => {
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [idCounter, setIdCounter] = useState(0);

    const onConnect = useCallback(
        (params) => setEdges((eds) => addEdge(params, eds)),
        [setEdges]
    );

    const isValidConnection = useCallback((connection) => {
        const sourceNode = nodes.find((node) => node.id === connection.source);
        const targetNode = nodes.find((node) => node.id === connection.target);

        // Permite m√∫ltiplas conex√µes para perguntas e condi√ß√µes
        if (sourceNode?.type === 'question' || sourceNode?.type === 'condition') {
            return true;
        }

        // Verifica se o n√≥ de origem j√° tem uma conex√£o (para outros tipos de n√≥s)
        const hasExistingConnection = edges.some(
            (edge) => edge.source === connection.source
        );

        return !hasExistingConnection;
    }, [nodes, edges]);

    const deleteNode = useCallback((nodeId) => {
        setNodes((nds) => nds.filter((node) => node.id !== nodeId));
        setEdges((eds) => eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId));
    }, [setNodes, setEdges]);

    const updateNode = useCallback((nodeId, newData) => {
        setNodes((prevNodes) =>
            prevNodes.map((node) =>
                node.id === nodeId ? { ...node, data: { ...node.data, ...newData } } : node
            )
        );
    }, [setNodes]);

    const createNode = useCallback((type, initialData) => {
        const nodeId = `${type}-${idCounter}`;
        setIdCounter((prev) => prev + 1);

        const updateFunctions = {
            message: {
                updateContent: (content) => updateNode(nodeId, { content }),
                onDelete: deleteNode,
            },
            question: {
                updateQuestion: (question) => updateNode(nodeId, { question }),
                addOption: () => {
                    setNodes((prevNodes) =>
                        prevNodes.map((node) =>
                            node.id === nodeId
                                ? {
                                    ...node,
                                    data: {
                                        ...node.data,
                                        options: [...(node.data.options || []), `Op√ß√£o ${node.data.options?.length + 1 || 1}`],
                                    },
                                }
                                : node
                        )
                    );
                },
                updateOptions: (options) => updateNode(nodeId, { options }),
                onDelete: deleteNode,
            },
            media: {
                handleFileUpload: (file) => updateNode(nodeId, { file }),
                updateUrl: (url) => updateNode(nodeId, { url }),
                onDelete: deleteNode,
            },
            condition: {
                updateConditionType: (conditionType) => updateNode(nodeId, { conditionType }),
                updateField: (field) => updateNode(nodeId, { field }),
                updateComparison: (comparison) => updateNode(nodeId, { comparison }),
                onDelete: deleteNode,
            },
            interval: {
                updateDuration: (duration) => updateNode(nodeId, { duration }),
                onDelete: deleteNode,
            },
            chatgpt: {
                updateApiKey: (apiKey) => updateNode(nodeId, { apiKey }),
                updateInstructions: (instructions) => updateNode(nodeId, { instructions }),
                onDelete: deleteNode,
            },
            api: {
                updateMethod: (method) => updateNode(nodeId, { method }),
                updateUrl: (url) => updateNode(nodeId, { url }),
                updateHeaders: (headers) => updateNode(nodeId, { headers }),
                updateBody: (body) => updateNode(nodeId, { body }),
                onDelete: deleteNode,
            },
            updateCRM: {
                updateEntity: (entity) => updateNode(nodeId, { entity }),
                updateField: (field) => updateNode(nodeId, { field }),
                updateValue: (value) => updateNode(nodeId, { value }),
                onDelete: deleteNode,
            },
            createRecord: {
                updateEntity: (entity) => updateNode(nodeId, { entity }),
                updateStage: (stage) => updateNode(nodeId, { stage }),
                updateFields: (fields) => updateNode(nodeId, { fields }),
                onDelete: deleteNode,
            },
            preConfigured: {
                onDelete: deleteNode,
            },
        };

        return {
            id: nodeId,
            type,
            position: { x: Math.random() * 400, y: Math.random() * 400 },
            draggable: true,
            data: {
                ...initialData,
                ...updateFunctions[type],
            },
        };
    }, [idCounter, updateNode, deleteNode, setNodes]);

    const addNode = useCallback((type) => {
        const nodeConfig = {
            message: { content: '' },
            question: { question: '', options: ['Sim', 'N√£o'] },
            media: { url: '', file: null },
            condition: { conditionType: 'cadastro', field: '', comparison: '' },
            interval: { duration: 300 },
            chatgpt: { apiKey: '', instructions: '' },
            api: { method: 'GET', url: '', headers: '', body: '' },
            updateCRM: { entity: 'lead', field: '', value: '' },
            createRecord: { entity: 'lead', stage: 'new', fields: '{}' },
            preConfigured: {}, // N√≥ pr√©-configurado n√£o precisa de configura√ß√µes iniciais
        };
        setNodes((prevNodes) => [...prevNodes, createNode(type, nodeConfig[type])]);
    }, [createNode, setNodes]);

    return (
        <div style={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
            <div style={{
                padding: '10px',
                backgroundColor: '#f8f9fa',
                borderBottom: '1px solid #ddd',
                display: 'flex',
                justifyContent: 'space-between',
                flexWrap: 'wrap'
            }}>
                <div>
                    <h3>Conversa</h3>
                    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                        <button style={topBarButtonStyle} onClick={() => addNode('message')}>Mensagem</button>
                        <button style={topBarButtonStyle} onClick={() => addNode('question')}>Pergunta</button>
                        <button style={topBarButtonStyle} onClick={() => addNode('media')}>M√≠dia</button>
                        <button style={topBarButtonStyle} onClick={() => addNode('condition')}>Condi√ß√£o</button>
                        <button style={topBarButtonStyle} onClick={() => addNode('interval')}>Intervalo</button>
                    </div>
                </div>
                <div>
                    <h3>Integra√ß√µes</h3>
                    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                        <button style={topBarButtonStyle} onClick={() => addNode('chatgpt')}>Chat GPT</button>
                        <button style={topBarButtonStyle} onClick={() => addNode('api')}>API Externa</button>
                    </div>
                </div>
                <div>
                    <h3>Bitrix</h3>
                    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                        <button style={topBarButtonStyle} onClick={() => addNode('updateCRM')}>Atualizar CRM</button>
                        <button style={topBarButtonStyle} onClick={() => addNode('createRecord')}>Criar Cadastro</button>
                    </div>
                </div>
            </div>

            {/* Container para o React Flow */}
            <div style={flowContainerStyles}>
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    isValidConnection={isValidConnection}
                    nodeTypes={nodeTypes}
                    fitView
                >
                    <MiniMap />
                    <Controls />
                    <Background />
                </ReactFlow>
            </div>
            <button
                style={{
                    position: 'absolute',
                    bottom: '20px',
                    right: '20px',
                    padding: '10px 20px',
                    backgroundColor: '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    zIndex: 1000
                }}
                onClick={() => addNode('preConfigured')}
            >
                Adicionar N√≥ Pr√©-configurado
            </button>
        </div>
    );
};

export default FlowEditor;
