// src/FlowEditor.js
import React, { useCallback } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from "react-flow-renderer";
import { saveFlow } from "./services";

const nodeTypes = {
  message: ({ data }) => (
    <div style={{ padding: 10, backgroundColor: "#f9f9f9", borderRadius: 5 }}>
      <strong>{data.label}</strong>
    </div>
  ),
};

const FlowEditor = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const addNode = (type, label, position) => {
    const newNode = {
      id: `node-${nodes.length + 1}`,
      type,
      data: { label },
      position,
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const handleSaveFlow = async () => {
    const flowData = { nodes, edges };
    await saveFlow(flowData);
    alert("Fluxo salvo com sucesso!");
  };

  return (
    <div style={{ height: "100vh" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>

      <div style={{ position: "absolute", top: 20, left: 20, zIndex: 1000 }}>
        <button onClick={() => addNode("message", "Mensagem", { x: 100, y: 100 })}>
          Adicionar Mensagem
        </button>
        <button onClick={() => addNode("question", "Pergunta", { x: 100, y: 200 })}>
          Adicionar Pergunta
        </button>
        <button onClick={handleSaveFlow}>Salvar Fluxo</button>
      </div>
    </div>
  );
};

export default FlowEditor;
