// src/FlowEditor.js
import React from "react";
import ReactFlow, { Handle, addEdge, MiniMap, Controls } from "react-flow-renderer";

const FlowEditor = ({ elements, setElements }) => {
  const onConnect = (params) => setElements((els) => addEdge(params, els));

  // Função para adicionar um nó de mensagem
  const addNode = (type) => {
    const newNode = {
      id: `node-${elements.length + 1}`,
      type: type,  // 'message' ou 'question'
      data: { label: `Novo Node ${elements.length + 1}` },
      position: { x: 100, y: 100 + elements.length * 150 }, // Ajustando a posição para ser visível
    };
    setElements((els) => [...els, newNode]);
  };

  // Função para adicionar um nó de pergunta com opções
  const addQuestionNode = (options) => {
    const newQuestionNode = {
      id: `node-${elements.length + 1}`,
      type: "question", // Tipo personalizado para perguntas
      data: {
        label: "Escolha uma opção",
        options: options, // Passa as opções para o nó
      },
      position: { x: 200, y: 200 + elements.length * 150 }, // Ajustando a posição
    };
    setElements((els) => [...els, newQuestionNode]);
  };

  return (
    <div style={{ height: "100%" }}>
      <ReactFlow
        elements={elements}
        onConnect={onConnect}
        style={{ height: "100%" }}
      >
        <MiniMap />
        <Controls />

        {/* Botões para adicionar mensagem ou pergunta */}
        <button onClick={() => addNode("message")}>Adicionar Mensagem</button>
        <button onClick={() => addQuestionNode(["Vendas", "Cobrança", "E-commerce"])}>Adicionar Pergunta</button>
      </ReactFlow>
    </div>
  );
};

export default FlowEditor;
