const nodeTypes = {
  message: MessageNode,
  question: QuestionNode,
  media: MediaNode,
  condition: ConditionNode,
  interval: IntervalNode,
  chatgpt: ChatGPTNode,
  api: APINode,
  updateCRM: UpdateCRMNode,
  createRecord: CreateRecordNode
};

const FlowEditor = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [idCounter, setIdCounter] = useState(0);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const isValidConnection = useCallback(
    (connection) => {
      const sourceNode = nodes.find((node) => node.id === connection.source);
      const targetNode = nodes.find((node) => node.id === connection.target);

      // Permite mÃºltiplas conexÃµes para perguntas e condiÃ§Ãµes
      if (sourceNode?.type === 'question' || sourceNode?.type === 'condition') {
        return true;
      }

      // Verifica se o nÃ³ de origem jÃ¡ tem uma conexÃ£o (para outros tipos de nÃ³s)
      const hasExistingConnection = edges.some(
        (edge) => edge.source === connection.source
      );

      return !hasExistingConnection;
    },
    [nodes, edges]
  );

  const deleteNode = useCallback(
    (nodeId) => {
      setNodes((nds) => nds.filter((node) => node.id !== nodeId));
      setEdges((eds) =>
        eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId)
      );
    },
    [setNodes, setEdges]
  );

  const updateNode = useCallback(
    (nodeId, newData) => {
      setNodes((prevNodes) =>
        prevNodes.map((node) =>
          node.id === nodeId
            ? { ...node, data: { ...node.data, ...newData } }
            : node
        )
      );
    },
    [setNodes]
  );

  const createNode = useCallback(
    (type, initialData) => {
      const nodeId = `${type}-${idCounter}`;
      setIdCounter((prev) => prev + 1);

      const updateFunctions = {
        message: {
          updateContent: (content) => updateNode(nodeId, { content }),
          onDelete: deleteNode
        },
        question: {
          updateQuestion: (question) => updateNode(nodeId, { question }),
          addOption: () => {
            setNodes((prevNodes) =>
              prevNodes.map((node) =>
                node.id === nodeId
                  ? {
                      ...node,
                      data: {
                        ...node.data,
                        options: [
                          ...(node.data.options || []),
                          `OpÃ§Ã£o ${
                            node.data.options ? node.data.options.length + 1 : 1
                          }`
                        ]
                      }
                    }
                  : node
              )
            );
          },
          updateOptions: (options) => updateNode(nodeId, { options }),
          onDelete: deleteNode
        },
        media: {
          handleFileUpload: (file) => updateNode(nodeId, { file }),
          updateUrl: (url) => updateNode(nodeId, { url }),
          onDelete: deleteNode
        },
        condition: {
          updateConditionType: (conditionType) =>
            updateNode(nodeId, { conditionType }),
          updateField: (field) => updateNode(nodeId, { field }),
          updateComparison: (comparison) => updateNode(nodeId, { comparison }),
          onDelete: deleteNode
        },
        interval: {
          updateDuration: (duration) => updateNode(nodeId, { duration }),
          onDelete: deleteNode
        },
        chatgpt: {
          updateApiKey: (apiKey) => updateNode(nodeId, { apiKey }),
          updateInstructions: (instructions) =>
            updateNode(nodeId, { instructions }),
          onDelete: deleteNode
        },
        api: {
          updateMethod: (method) => updateNode(nodeId, { method }),
          updateUrl: (url) => updateNode(nodeId, { url }),
          updateHeaders: (headers) => updateNode(nodeId, { headers }),
          updateBody: (body) => updateNode(nodeId, { body }),
          onDelete: deleteNode
        },
        updateCRM: {
          updateEntity: (entity) => updateNode(nodeId, { entity }),
          updateField: (field) => updateNode(nodeId, { field }),
          updateValue: (value) => updateNode(nodeId, { value }),
          onDelete: deleteNode
        },
        createRecord: {
          updateEntity: (entity) => updateNode(nodeId, { entity }),
          updateStage: (stage) => updateNode(nodeId, { stage }),
          updateFields: (fields) => updateNode(nodeId, { fields }),
          onDelete: deleteNode
        }
      };

      const newNode = {
        id: nodeId,
        type,
        data: { ...initialData, ...updateFunctions[type] },
        position: { x: Math.random() * 400, y: Math.random() * 400 }
      };

      setNodes((nds) => [...nds, newNode]);
    },
    [idCounter, updateNode, deleteNode, setNodes]
  );

  return (
    <div>
      <div style={{ display: 'flex', padding: '10px', background: '#eee' }}>
        <button
          style={topBarButtonStyle}
          onClick={() => createNode('message', { content: '' })}
        >
          Mensagem
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() =>
            createNode('question', { question: '', options: [] })
          }
        >
          Pergunta
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() => createNode('media', { url: '' })}
        >
          MÃ­dia
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() =>
            createNode('condition', {
              conditionType: 'cadastro',
              field: '',
              comparison: ''
            })
          }
        >
          CondiÃ§Ã£o
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() => createNode('interval', { duration: 1 })}
        >
          Intervalo
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() =>
            createNode('chatgpt', { apiKey: '', instructions: '' })
          }
        >
          Chat GPT
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() =>
            createNode('api', { method: 'GET', url: '', headers: '', body: '' })
          }
        >
          API Externa
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() =>
            createNode('updateCRM', { entity: 'lead', field: '', value: '' })
          }
        >
          Atualizar CRM
        </button>
        <button
          style={topBarButtonStyle}
          onClick={() =>
            createNode('createRecord', { entity: 'lead', stage: 'new', fields: '' })
          }
        >
          Criar Cadastro
        </button>
      </div>
      <div style={flowContainerStyles}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
          connectionLineStyle={{ stroke: '#ddd', strokeWidth: 2 }}
          snapToGrid={true}
          snapGrid={[15, 15]}
          isValidConnection={isValidConnection}
        >
          <MiniMap />
          <Controls />
          <Background color="#aaa" gap={16} />
        </ReactFlow>
      </div>
    </div>
  );
};

export default FlowEditor;
