import React, { useState, useCallback } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    Handle,
    useNodesState,
    useEdgesState,
    addEdge
} from 'react-flow-renderer';

// Estilos atualizados
const flowContainerStyles = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    overflow: 'hidden'
};

const nodeStyles = {
    base: {
        padding: '20px',
        backgroundColor: '#fff',
        borderRadius: '8px',
        border: '2px solid #2684ff',
        minWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    input: {
        margin: '10px 0',
        padding: '8px',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd'
    },
    section: {
        margin: '15px 0',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '6px',
        width: '100%'
    },
    select: {
        width: '100%',
        padding: '8px',
        margin: '5px 0'
    },
    deleteButton: {
        background: 'red',
        color: 'white',
        border: 'none',
        borderRadius: '50%',
        width: '24px',
        height: '24px',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: '10px'
    }
};

// Componentes dos N√≥s com React.memo para otimiza√ß√£o
const MessageNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="left" />
        <h4>üí¨ Mensagem</h4>
        <textarea
            style={{ ...nodeStyles.input, height: '100px' }}
            value={data.content}
            onChange={(e) => data.updateContent(e.target.value)}
            placeholder="Escreva sua mensagem..."
        />
        <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>üóëÔ∏è</button>
        <Handle type="source" position="right" />
    </div>
));

const QuestionNode = React.memo(({ id, data }) => {
    // Fun√ß√£o para criar um novo caminho
    const handleAddOption = () => {
        const currentOptions = data.options || [];
        const newOption = `Op√ß√£o ${currentOptions.length + 1}`;
        data.addOption(newOption); // Adiciona uma nova op√ß√£o
    };

    return (
        <div style={nodeStyles.base}>
            <Handle type="target" position="left" />
            <h4>‚ùì Pergunta</h4>
            <input
                type="text"
                style={nodeStyles.input}
                value={data.question}
                onChange={(e) => data.updateQuestion(e.target.value)}
                placeholder="Digite a pergunta"
            />
            <div style={nodeStyles.section}>
                <h5>Op√ß√µes de Resposta:</h5>
                {data.options.map((opt, index) => (
                    <div key={index} style={{ margin: '5px 0' }}>
                        <input
                            type="text"
                            value={opt}
                            style={{ ...nodeStyles.input, width: '80%' }}
                            onChange={(e) => data.updateOption(index, e.target.value)}
                        />
                        <Handle
                            type="source"
                            position="right"
                            id={`option-${index}`}
                            style={{ left: '85%' }}
                        />
                    </div>
                ))}
                <button onClick={handleAddOption}>+ Adicionar Op√ß√£o</button>
            </div>
            <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>üóëÔ∏è</button>
            <Handle type="source" position="right" />
        </div>
    );
});

const MediaNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="left" />
        <h4>üñºÔ∏è M√≠dia</h4>
        <input
            type="file"
            onChange={(e) => data.handleFileUpload(e.target.files[0])}
            style={nodeStyles.input}
        />
        <div style={nodeStyles.section}>
            <h5>Ou insira URL:</h5>
            <input
                type="text"
                value={data.url}
                onChange={(e) => data.updateUrl(e.target.value)}
                placeholder="https://exemplo.com/imagem.jpg"
                style={nodeStyles.input}
            />
        </div>
        <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>üóëÔ∏è</button>
        <Handle type="source" position="right" />
    </div>
));

// Outros n√≥s omitidos para brevidade...

const FlowEditor = () => {
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [idCounter, setIdCounter] = useState(0);

    const onConnect = useCallback(
        (params) => setEdges((eds) => addEdge(params, eds)),
        [setEdges]
    );

    const isValidConnection = useCallback((connection) => {
        const sourceNode = nodes.find((node) => node.id === connection.source);
        const targetNode = nodes.find((node) => node.id === connection.target);

        // Permite m√∫ltiplas conex√µes para perguntas e condi√ß√µes
        if (sourceNode.type === 'question' || sourceNode.type === 'condition') {
            return true;
        }

        // Verifica se o n√≥ de origem j√° tem uma conex√£o
        const hasExistingConnection = edges.some(
            (edge) => edge.source === connection.source
        );

        return !hasExistingConnection;
    }, [nodes, edges]);

    const deleteNode = useCallback((nodeId) => {
        setNodes((nds) => nds.filter((node) => node.id !== nodeId));
        setEdges((eds) => eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId));
    }, [setNodes, setEdges]);

    const updateNode = useCallback((nodeId, newData) => {
        setNodes((prevNodes) =>
            prevNodes.map((node) =>
                node.id === nodeId ? { ...node, data: { ...node.data, ...newData } } : node
            )
        );
    }, [setNodes]);

    const createNode = useCallback((type, initialData) => {
        const nodeId = `${type}-${idCounter}`;
        setIdCounter((prev) => prev + 1);

        const updateFunctions = {
            message: {
                updateContent: (content) => updateNode(nodeId, { content }),
                onDelete: deleteNode,
            },
            question: {
                updateQuestion: (question) => updateNode(nodeId, { question }),
                addOption: (newOption) => {
                    const currentOptions = initialData.options || [];
                    updateNode(nodeId, { options: [...currentOptions, newOption] });
                },
                updateOption: (index, value) => {
                    const newOptions = [...(initialData.options || [])];
                    newOptions[index] = value;
                    updateNode(nodeId, { options: newOptions });
                },
                onDelete: deleteNode,
            },
            media: {
                handleFileUpload: (file) => updateNode(nodeId, { file }),
                updateUrl: (url) => updateNode(nodeId, { url }),
                onDelete: deleteNode,
            },
            condition: {
                updateConditionType: (conditionType) => updateNode(nodeId, { conditionType }),
                updateField: (field) => updateNode(nodeId, { field }),
                updateComparison: (comparison) => updateNode(nodeId, { comparison }),
                onDelete: deleteNode,
            },
            interval: {
                updateDuration: (duration) => updateNode(nodeId, { duration }),
                onDelete: deleteNode,
            },
            chatgpt: {
                updateApiKey: (apiKey) => updateNode(nodeId, { apiKey }),
                updateInstructions: (instructions) => updateNode(nodeId, { instructions }),
                onDelete: deleteNode,
            },
            api: {
                updateMethod: (method) => updateNode(nodeId, { method }),
                updateUrl: (url) => updateNode(nodeId, { url }),
                updateHeaders: (headers) => updateNode(nodeId, { headers }),
                updateBody: (body) => updateNode(nodeId, { body }),
                onDelete: deleteNode,
            },
            updateCRM: {
                updateEntity: (entity) => updateNode(nodeId, { entity }),
                updateField: (field) => updateNode(nodeId, { field }),
                updateValue: (value) => updateNode(nodeId, { value }),
                onDelete: deleteNode,
            },
            createRecord: {
                updateEntity: (entity) => updateNode(nodeId, { entity }),
                updateStage: (stage) => updateNode(nodeId, { stage }),
                updateFields: (fields) => updateNode(nodeId, { fields }),
                onDelete: deleteNode,
            },
        };

        return {
            id: nodeId,
            type,
            position: { x: Math.random() * 400, y: Math.random() * 400 },
            draggable: true,
            data: {
                ...initialData,
                ...updateFunctions[type],
            },
        };
    }, [idCounter, updateNode, deleteNode]);

    const addNode = useCallback((type) => {
        const nodeConfig = {
            message: { content: '' },
            question: { question: '', options: ['Sim', 'N√£o'] },
            media: { url: '', file: null },
            condition: { conditionType: 'cadastro', field: '', comparison: '' },
            interval: { duration: 300 },
            chatgpt: { apiKey: '', instructions: '' },
            api: { method: 'GET', url: '', headers: '', body: '' },
            updateCRM: { entity: 'lead', field: '', value: '' },
            createRecord: { entity: 'lead', stage: 'new', fields: '{}' },
        };
        setNodes((prevNodes) => [...prevNodes, createNode(type, nodeConfig[type])]);
    }, [createNode, setNodes]);

    return (
        <div style={{ height: '100vh', display: 'flex' }}>
            <div style={{
                width: '250px',
                padding: '20px',
                backgroundColor: '#f8f9fa',
                borderRight: '1px solid #ddd',
                overflowY: 'auto'
            }}>
                <h3>Ferramentas</h3>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    <button onClick={() => addNode('message')}>Nova Mensagem</button>
                    <button onClick={() => addNode('question')}>Nova Pergunta</button>
                    <button onClick={() => addNode('media')}>Nova M√≠dia</button>
                    <button onClick={() => addNode('condition')}>Nova Condi√ß√£o</button>
                    <button onClick={() => addNode('interval')}>Novo Intervalo</button>
                    <button onClick={() => addNode('chatgpt')}>Chat GPT</button>
                    <button onClick={() => addNode('api')}>API Externa</button>
                    <button onClick={() => addNode('updateCRM')}>Atualizar CRM</button>
                    <button onClick={() => addNode('createRecord')}>Criar Cadastro</button>
                </div>
            </div>

            {/* Container para o React Flow */}
            <div style={flowContainerStyles}>
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    isValidConnection={isValidConnection}
                    nodeTypes={nodeTypes}
                    fitView
                >
                    <MiniMap />
                    <Controls />
                    <Background />
                </ReactFlow>
            </div>
        </div>
    );
};

export default FlowEditor;
