// src/FlowEditor.js
import React, { useState, useCallback } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
} from "react-flow-renderer";
import { saveFlow } from "./services";

// Definindo os tipos de nós
const nodeTypes = {
  message: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  question: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  condition: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  transfer: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  end: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
};

const FlowEditor = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  // Função para conectar nós
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // Função para adicionar um nó
  const addNode = (type, label, position) => {
    const newNode = {
      id: `node-${nodes.length + 1}`,
      type,
      data: { label },
      position,
    };
    setNodes((nds) => [...nds, newNode]);
  };

  // Função para salvar o fluxo
  const handleSaveFlow = async () => {
    const flowData = { nodes, edges };
    await saveFlow(flowData);
    alert("Fluxo salvo com sucesso!");
  };

  return (
    <div style={{ height: "100vh", display: "flex" }}>
      {/* Painel lateral com opções de interação */}
      <div style={styles.sidebar}>
        <h3>Interações</h3>
        <button
          onClick={() => addNode("message", "Mensagem", { x: 100, y: 100 })}
          style={styles.sidebarButton}
        >
          Mensagem
        </button>
        <button
          onClick={() => addNode("question", "Pergunta", { x: 100, y: 200 })}
          style={styles.sidebarButton}
        >
          Pergunta
        </button>
        <button
          onClick={() => addNode("condition", "Condição", { x: 100, y: 300 })}
          style={styles.sidebarButton}
        >
          Condição
        </button>
        <button
          onClick={() => addNode("transfer", "Transferir", { x: 100, y: 400 })}
          style={styles.sidebarButton}
        >
          Transferir
        </button>
        <button
          onClick={() => addNode("end", "Finalizar", { x: 100, y: 500 })}
          style={styles.sidebarButton}
        >
          Finalizar
        </button>
        <button onClick={handleSaveFlow} style={styles.saveButton}>
          Salvar Fluxo
        </button>
      </div>

      {/* Área do React Flow */}
      <div style={{ flex: 1, position: "relative" }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </div>
    </div>
  );
};

// Estilos
const styles = {
  sidebar: {
    width: "250px",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  sidebarButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  saveButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "20px 0",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  node: {
    padding: "10px",
    backgroundColor: "#fff",
    borderRadius: "5px",
    border: "1px solid #ddd",
  },
};

export default FlowEditor;
