// src/services.js
import axios from "axios";

// const BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:5000";

export const saveFlow = async (flowData) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/save_flow`, flowData);
    return response.data;
  } catch (error) {
    console.error("Erro ao salvar fluxo", error);
    throw new Error("Não foi possível salvar o fluxo. Tente novamente mais tarde.");
  }
};

export const sendMessage = async (message) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/send_message`, { message });
    return response.data;
  } catch (error) {
    console.error("Erro ao enviar mensagem", error);
    throw new Error("Não foi possível enviar a mensagem. Tente novamente mais tarde.");
  }
};
