// src/FlowEditor.js
import React, { useState, useCallback } from 'react';
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  Handle,
  useNodesState,
  useEdgesState
} from 'react-flow-renderer';
import { saveFlow } from './services';

const nodeStyles = {
  node: {
    padding: '20px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    border: '2px solid #2684ff',
    minWidth: '200px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
  },
  input: {
    marginTop: '10px',
    padding: '8px',
    width: '100%',
    borderRadius: '4px',
    border: '1px solid #ddd'
  },
  optionsContainer: {
    marginTop: '15px',
    display: 'flex',
    flexDirection: 'column',
    gap: '8px'
  }
};

const MessageNode = ({ data }) => (
  <div style={nodeStyles.node}>
    <Handle type="target" position="top" />
    <strong>{data.label}</strong>
    <textarea
      style={{ ...nodeStyles.input, height: '80px' }}
      value={data.content}
      onChange={(e) => data.updateContent(e.target.value)}
      placeholder="Digite a mensagem..."
    />
    <Handle type="source" position="bottom" />
  </div>
);

const QuestionNode = ({ data }) => (
  <div style={nodeStyles.node}>
    <Handle type="target" position="top" />
    <strong>{data.label}</strong>
    <textarea
      style={{ ...nodeStyles.input, height: '60px' }}
      value={data.content}
      onChange={(e) => data.updateQuestion(e.target.value)}
      placeholder="Digite a pergunta..."
    />
    <div style={nodeStyles.optionsContainer}>
      {data.options.map((option, index) => (
        <div key={index} style={{ position: 'relative' }}>
          <input
            type="text"
            value={option.label}
            onChange={(e) => data.updateOption(index, e.target.value)}
            style={nodeStyles.input}
            placeholder="Opção"
          />
          <Handle
            type="source"
            position="bottom"
            id={option.value}
            style={{ left: '50%', transform: 'translateX(-50%)' }}
          />
        </div>
      ))}
    </div>
  </div>
);

const FlowEditor = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [idCounter, setIdCounter] = useState(0);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const updateNodeData = (nodeId, newData) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === nodeId) {
          return { ...node, data: { ...node.data, ...newData } };
        }
        return node;
      })
    );
  };

  const addNode = (type, label) => {
    const newNode = {
      id: `${type}-${idCounter}`,
      type,
      position: { x: Math.random() * 400, y: Math.random() * 400 },
      draggable: true,
      data: {
        label,
        content: '',
        options: [{ label: 'Sim', value: 'yes' }, { label: 'Não', value: 'no' }],
        updateContent: (content) => updateNodeData(`${type}-${idCounter}`, { content }),
        updateQuestion: (content) => updateNodeData(`${type}-${idCounter}`, { content }),
        updateOption: (index, label) => {
          const newOptions = [...newNode.data.options];
          newOptions[index].label = label;
          updateNodeData(`${type}-${idCounter}`, { options: newOptions });
        }
      }
    };
    setIdCounter(idCounter + 1);
    setNodes((nds) => [...nds, newNode]);
  };

  const handleSave = async () => {
    try {
      await saveFlow({ nodes, edges });
      alert('Fluxo salvo com sucesso!');
    } catch (error) {
      alert('Erro ao salvar fluxo!');
    }
  };

  return (
    <div style={{ height: '100vh', display: 'flex' }}>
      <div style={{
        width: '250px',
        padding: '20px',
        backgroundColor: '#f5f7fa',
        borderRight: '1px solid #ddd'
      }}>
        <h3>Ações</h3>
        <button onClick={() => addNode('message', 'Mensagem')}>Nova Mensagem</button>
        <button onClick={() => addNode('question', 'Pergunta')}>Nova Pergunta</button>
        <button onClick={handleSave} style={{ marginTop: '20px', backgroundColor: '#28a745' }}>
          Salvar Fluxo
        </button>
      </div>

      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={{ message: MessageNode, question: QuestionNode }}
        fitView
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
    </div>
  );
};

export default FlowEditor;
