// src/FlowEditor.js
import React, { useCallback, useState } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from "react-flow-renderer";
import { saveFlow } from "./services";

const nodeTypes = {
  message: ({ data }) => (
    <div style={{ padding: 10, backgroundColor: "#fff", borderRadius: "5px", border: "1px solid #ddd" }}>
      <strong>{data.label}</strong>
    </div>
  ),
  question: ({ data }) => (
    <div style={{ padding: 10, backgroundColor: "#fff", borderRadius: "5px", border: "1px solid #ddd" }}>
      <strong>{data.label}</strong>
    </div>
  ),
};

const FlowEditor = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const addNode = (type, label, position) => {
    const newNode = {
      id: `node-${nodes.length + 1}`,
      type,
      data: { label },
      position,
    };
    setNodes((nds) => [...nds, newNode]);
  };

  const handleSaveFlow = async () => {
    const flowData = { nodes, edges };
    await saveFlow(flowData);
    alert("Fluxo salvo com sucesso!");
  };

  return (
    <div style={{ height: "100vh", display: "flex" }}>
      <div style={{ width: "250px", padding: "20px", backgroundColor: "#fff", borderRadius: "10px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
        <h3>Interações</h3>
        <button
          onClick={() => addNode("message", "Mensagem", { x: 100, y: 100 })}
          style={styles.sidebarButton}
        >
          Mensagem
        </button>
        <button
          onClick={() => addNode("question", "Pergunta", { x: 100, y: 200 })}
          style={styles.sidebarButton}
        >
          Pergunta
        </button>
        <button
          onClick={() => addNode("condition", "Condição", { x: 100, y: 300 })}
          style={styles.sidebarButton}
        >
          Condição
        </button>
        <button onClick={handleSaveFlow} style={styles.saveButton}>
          Salvar Fluxo
        </button>
      </div>

      <div style={{ flex: 1, position: "relative" }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </div>
    </div>
  );
};

const styles = {
  sidebarButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  saveButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "20px 0",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
};

export default FlowEditor;
