import React, { useState, useCallback } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    Handle,
    useNodesState,
    useEdgesState,
    addEdge
} from 'react-flow-renderer';

// Estilos atualizados
const flowContainerStyles = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    overflow: 'hidden'
};

const nodeStyles = {
    base: {
        padding: '20px',
        backgroundColor: '#fff',
        borderRadius: '8px',
        border: '2px solid #2684ff',
        minWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
    },
    input: {
        margin: '10px 0',
        padding: '8px',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd'
    },
    section: {
        margin: '15px 0',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '6px',
        width: '100%'
    },
    select: {
        width: '100%',
        padding: '8px',
        margin: '5px 0'
    },
    deleteButton: {
        background: 'red',
        color: 'white',
        border: 'none',
        borderRadius: '50%',
        width: '24px',
        height: '24px',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: '10px'
    }
};

// Estilo personalizado para os Handles (bolinhas de conexão)
const handleStyle = {
    width: '15px',
    height: '15px',
    backgroundColor: '#2684ff',
    border: '2px solid #fff',
    borderRadius: '50%'
};

// Novo estilo para os botões da barra lateral
const sidebarButtonStyle = {
    padding: '12px 18px', // Aumentando o padding para torná-los maiores
    fontSize: '18px', // Aumentando o tamanho da fonte
    backgroundColor: '#007bff', // Cor azul
    color: 'white',
    border: '2px solid red', // Adicionando borda vermelha
    borderRadius: '5px',
    cursor: 'pointer',
    width: '100%',
    textAlign: 'left'
};

// Componentes dos Nós com React.memo para otimização
const MessageNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="left" style={handleStyle} />
        <h4>💬 Mensagem</h4>
        <textarea
            style={{ ...nodeStyles.input, height: '100px' }}
            value={data.content}
            onChange={(e) => data.updateContent(e.target.value)}
            placeholder="Escreva sua mensagem..."
        />
        <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>🗑️</button>
        <Handle type="source" position="right" style={handleStyle} />
    </div>
));

const QuestionNode = React.memo(({ id, data }) => {
    const handleOptionChange = (index, value) => {
        const newOptions = [...data.options];
        newOptions[index] = value;
        data.updateOptions(newOptions);
    };

    return (
        <div style={nodeStyles.base}>
            <Handle type="target" position="left" style={handleStyle} />
            <h4>❓ Pergunta</h4>
            <input
                type="text"
                style={nodeStyles.input}
                value={data.question}
                onChange={(e) => data.updateQuestion(e.target.value)}
                placeholder="Digite a pergunta"
            />
            <div style={nodeStyles.section}>
                <h5>Opções de Resposta:</h5>
                {data.options.map((opt, index) => (
                    <div key={index} style={{ margin: '5px 0', position: 'relative' }}>
                        <input
                            type="text"
                            value={opt}
                            style={{ ...nodeStyles.input, width: '80%' }}
                            onChange={(e) => handleOptionChange(index, e.target.value)}
                        />
                        <Handle
                            type="source"
                            position="right"
                            id={`option-${index}`}
                            style={{ ...handleStyle, right: '-8px', top: '50%', transform: 'translateY(-50%)' }}
                        />
                    </div>
                ))}
                <button onClick={data.addOption}>+ Adicionar Opção</button>
            </div>
            <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>🗑️</button>
        </div>
    );
});

const MediaNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="left" style={handleStyle} />
        <h4>🖼️ Mídia</h4>
        <input
            type="file"
            onChange={(e) => data.handleFileUpload(e.target.files[0])}
            style={nodeStyles.input}
        />
        <div style={nodeStyles.section}>
            <h5>Ou insira URL:</h5>
            <input
                type="text"
                value={data.url}
                onChange={(e) => data.updateUrl(e.target.value)}
                placeholder="https://exemplo.com/imagem.jpg"
                style={nodeStyles.input}
            />
        </div>
        <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>🗑️</button>
        <Handle type="source" position="right" style={handleStyle} />
    </div>
));

const ConditionNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="left" style={handleStyle} />
        <h4>⚖️ Condição</h4>
        <select
            value={data.conditionType}
            onChange={(e) => data.updateConditionType(e.target.value)}
            style={nodeStyles.select}
        >
            <option value="cadastro">Verificar Cadastro</option>
            <option value="valor">Comparar Valores</option>
        </select>

        {data.conditionType === 'cadastro' && (
            <div style={nodeStyles.section}>
                <input
                    type="text"
                    value={data.field}
                    onChange={(e) => data.updateField(e.target.value)}
