// src/FlowEditor.js
import React, { useState, useCallback } from "react";
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
} from "react-flow-renderer";
import { saveFlow } from "./services";

// Definindo os tipos de nós
const nodeTypes = {
  message: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  question: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  condition: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  transfer: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  end: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  media: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  interval: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  chatgpt: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  api: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  update_crm: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
  create_record: ({ data }) => (
    <div style={styles.node}>
      <strong>{data.label}</strong>
    </div>
  ),
};

const FlowEditor = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [idCounter, setIdCounter] = useState(0); // Contador para gerar IDs únicos para os nós

  // Função para conectar nós
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  // Função para adicionar um nó
  const addNode = (type, label) => {
    const newNode = {
      id: `${type}-${idCounter}`, // ID único para cada nó
      type,
      data: { label },
      position: { x: Math.random() * 400, y: Math.random() * 400 }, // Posição aleatória
      draggable: true, // Permitir arrastar o nó
    };
    setIdCounter(idCounter + 1); // Atualiza o contador de IDs
    setNodes((nds) => [...nds, newNode]); // Adiciona o novo nó ao array existente

    // Conecta o nó ao nó anterior
    if (nodes.length > 0) {
      setEdges((eds) => [
        ...eds,
        {
          id: `edge-${nodes[nodes.length - 1].id}-${newNode.id}`,
          source: nodes[nodes.length - 1].id,
          target: newNode.id,
        },
      ]);
    }
  };

  // Função para salvar o fluxo
  const handleSaveFlow = async () => {
    const flowData = { nodes, edges };
    await saveFlow(flowData);
    alert("Fluxo salvo com sucesso!");
  };

  return (
    <div style={{ height: "100vh", display: "flex" }}>
      {/* Painel lateral com opções de interação */}
      <div style={styles.sidebar}>
        <h3>Interações</h3>
        <button onClick={() => addNode("message", "Mensagem")} style={styles.sidebarButton}>
          Mensagem
        </button>
        <button onClick={() => addNode("question", "Pergunta")} style={styles.sidebarButton}>
          Pergunta
        </button>
        <button onClick={() => addNode("media", "Mídia")} style={styles.sidebarButton}>
          Mídia
        </button>
        <button onClick={() => addNode("condition", "Condição")} style={styles.sidebarButton}>
          Condição
        </button>
        <button onClick={() => addNode("transfer", "Transferir")} style={styles.sidebarButton}>
          Transferir
        </button>
        <button onClick={() => addNode("interval", "Intervalo")} style={styles.sidebarButton}>
          Intervalo
        </button>
        <button onClick={() => addNode("chatgpt", "Chat GPT")} style={styles.sidebarButton}>
          Chat GPT
        </button>
        <button onClick={() => addNode("api", "API Externa")} style={styles.sidebarButton}>
          API Externa
        </button>
        <button onClick={() => addNode("update_crm", "Atualizar CRM")} style={styles.sidebarButton}>
          Atualizar CRM
        </button>
        <button onClick={() => addNode("create_record", "Criar Cadastro")} style={styles.sidebarButton}>
          Criar Cadastro
        </button>
        <button onClick={handleSaveFlow} style={styles.saveButton}>
          Salvar Fluxo
        </button>
      </div>

      {/* Área do React Flow */}
      <div style={{ flex: 1, position: "relative" }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onConnect={onConnect}
          onNodesChange={(changes) => {
            // Atualiza a posição dos nós quando eles são arrastados
            setNodes((nds) =>
              nds.map((node) => {
                const change = changes.find((c) => c.id === node.id);
                return change ? { ...node, position: change.position } : node;
              })
            );
          }}
          nodeTypes={nodeTypes}
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
      </div>
    </div>
  );
};

// Estilos
const styles = {
  sidebar: {
    width: "250px",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  sidebarButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "10px 0",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  saveButton: {
    display: "block",
    width: "100%",
    padding: "10px",
    margin: "20px 0",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
  node: {
    padding: "10px",
    backgroundColor: "#fff",
    borderRadius: "5px",
    border: "1px solid #ddd",
  },
};

export default FlowEditor;
