import React, { useState, useCallback, useMemo } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    Handle,
    useNodesState,
    useEdgesState
} from 'react-flow-renderer';
import { saveFlow } from './services';

// Estilos atualizados
const flowContainerStyles = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    overflow: 'hidden'
};

const nodeStyles = {
    base: {
        padding: '20px',
        backgroundColor: '#fff',
        borderRadius: '8px',
        border: '2px solid #2684ff',
        minWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
    },
    input: {
        margin: '10px 0',
        padding: '8px',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd'
    },
    section: {
        margin: '15px 0',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '6px'
    },
    select: {
        width: '100%',
        padding: '8px',
        margin: '5px 0'
    }
};

// Componentes dos N√≥s com React.memo para otimiza√ß√£o
const MessageNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>üí¨ Mensagem</h4>
        <textarea
            style={{ ...nodeStyles.input, height: '100px' }}
            value={data.content}
            onChange={data.updateContent}
            placeholder="Escreva sua mensagem..."
        />
        <Handle type="source" position="bottom" />
    </div>
));

const QuestionNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>‚ùì Pergunta</h4>
        <input
            type="text"
            style={nodeStyles.input}
            value={data.question}
            onChange={data.updateQuestion}
            placeholder="Digite a pergunta"
        />
        <div style={nodeStyles.section}>
            <h5>Op√ß√µes de Resposta:</h5>
            {data.options.map((opt, index) => (
                <div key={index} style={{ margin: '5px 0' }}>
                    <input
                        type="text"
                        value={opt}
                        style={{ ...nodeStyles.input, width: '80%' }}
                        onChange={(e) => data.updateOption(index, e.target.value)}
                    />
                    <Handle
                        type="source"
                        position="bottom"
                        id={`option-${index}`}
                        style={{ left: '85%' }}
                    />
                </div>
            ))}
            <button onClick={data.addOption}>+ Adicionar Op√ß√£o</button>
        </div>
    </div>
));

const MediaNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>üñºÔ∏è M√≠dia</h4>
        <input
            type="file"
            onChange={(e) => data.handleFileUpload(e.target.files[0])}
            style={nodeStyles.input}
        />
        <div style={nodeStyles.section}>
            <h5>Ou insira URL:</h5>
            <input
                type="text"
                value={data.url}
                onChange={data.updateUrl}
                placeholder="https://exemplo.com/imagem.jpg"
                style={nodeStyles.input}
            />
        </div>
        <Handle type="source" position="bottom" />
    </div>
));

const ConditionNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>‚öñÔ∏è Condi√ß√£o</h4>
        <select
            value={data.conditionType}
            onChange={data.updateConditionType}
            style={nodeStyles.select}
        >
            <option value="cadastro">Verificar Cadastro</option>
            <option value="valor">Comparar Valores</option>
        </select>

        {data.conditionType === 'cadastro' && (
            <div style={nodeStyles.section}>
                <input
                    type="text"
                    value={data.field}
                    onChange={data.updateField}
                    placeholder="Campo para verifica√ß√£o (ex: PHONE)"
                    style={nodeStyles.input}
                />
            </div>
        )}

        {data.conditionType === 'valor' && (
            <div style={nodeStyles.section}>
                <input
                    type="text"
                    value={data.comparison}
                    onChange={data.updateComparison}
                    placeholder="Express√£o (ex: VALUE > 100)"
                    style={nodeStyles.input}
                />
            </div>
        )}

        <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '15px' }}>
            <Handle type="source" position="bottom" id="true" style={{ left: '30%' }} />
            <Handle type="source" position="bottom" id="false" style={{ left: '70%' }} />
        </div>
    </div>
));

const IntervalNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>‚è±Ô∏è Intervalo</h4>
        <div style={nodeStyles.section}>
            <input
                type="number"
                value={data.duration}
                onChange={data.updateDuration}
                placeholder="Dura√ß√£o em segundos"
                min="1"
                max="7200"
                style={nodeStyles.input}
            />
            <small>M√°ximo: 2 horas (7200 segundos)</small>
        </div>
        <Handle type="source" position="bottom" />
    </div>
));

const ChatGPTNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>ü§ñ Chat GPT</h4>
        <input
            type="password"
            value={data.apiKey}
            onChange={data.updateApiKey}
            placeholder="Insira sua API Key da OpenAI"
            style={nodeStyles.input}
        />
        <textarea
            value={data.instructions}
            onChange={data.updateInstructions}
            placeholder="Instru√ß√µes para o assistente..."
            style={{ ...nodeStyles.input, height: '100px' }}
        />
        <Handle type="source" position="bottom" />
    </div>
));

const APINode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>üåê API Externa</h4>
        <div style={nodeStyles.section}>
            <select
                value={data.method}
                onChange={data.updateMethod}
                style={nodeStyles.select}
            >
                <option value="GET">GET</option>
                <option value="POST">POST</option>
                <option value="PUT">PUT</option>
                <option value="DELETE">DELETE</option>
            </select>
            <input
                type="text"
                value={data.url}
                onChange={data.updateUrl}
                placeholder="URL da API"
                style={nodeStyles.input}
            />
        </div>

        <div style={nodeStyles.section}>
            <h5>Cabe√ßalhos:</h5>
            <textarea
                value={data.headers}
                onChange={data.updateHeaders}
                placeholder="JSON de cabe√ßalhos"
                style={{ ...nodeStyles.input, height: '80px' }}
            />
        </div>

        <div style={nodeStyles.section}>
            <h5>Corpo da Requisi√ß√£o:</h5>
            <textarea
                value={data.body}
                onChange={data.updateBody}
                placeholder="JSON do corpo"
                style={{ ...nodeStyles.input, height: '100px' }}
            />
        </div>
        <Handle type="source" position="bottom" />
    </div>
));

const UpdateCRMNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>üîÑ Atualizar CRM</h4>
        <div style={nodeStyles.section}>
            <select
                value={data.entity}
                onChange={data.updateEntity}
                style={nodeStyles.select}
            >
                <option value="lead">Lead</option>
                <option value="deal">Neg√≥cio</option>
                <option value="contact">Contato</option>
                <option value="company">Empresa</option>
            </select>

            <input
                type="text"
                value={data.field}
                onChange={data.updateField}
                placeholder="Campo para atualizar"
                style={nodeStyles.input}
            />

            <input
                type="text"
                value={data.value}
                onChange={data.updateValue}
                placeholder="Novo valor"
                style={nodeStyles.input}
            />
        </div>
        <Handle type="source" position="bottom" />
    </div>
));

const CreateRecordNode = React.memo(({ data }) => (
    <div style={nodeStyles.base}>
        <Handle type="target" position="top" />
        <h4>üìù Criar Cadastro</h4>
        <div style={nodeStyles.section}>
            <select
                value={data.entity}
                onChange={data.updateEntity}
                style={nodeStyles.select}
            >
                <option value="lead">Lead</option>
                <option value="deal">Neg√≥cio</option>
                <option value="contact">Contato</option>
                <option value="company">Empresa</option>
            </select>

            <select
                value={data.stage}
                onChange={data.updateStage}
                style={nodeStyles.select}
            >
                <option value="new">Novo</option>
                <option value="qualified">Qualificado</option>
                <option value="proposal">Proposta</option>
                <option value="closed">Fechado</option>
            </select>

            <textarea
                value={data.fields}
                onChange={data.updateFields}
                placeholder="Campos (JSON)"
                style={{ ...nodeStyles.input, height: '150px' }}
            />
        </div>
        <Handle type="source" position="bottom" />
    </div>
));

const FlowEditor = () => {
    const [nodes, setNodes, onNodesChange] = useNodesState();
    const [edges, setEdges, onEdgesChange] = useEdgesState();
    const [idCounter, setIdCounter] = useState(0);

    const updateNode = useCallback((nodeId, newData) => {
        setNodes(prevNodes => {
            if (!Array.isArray(prevNodes)) {
                console.error("Previous nodes state is not an array:", prevNodes);
                return; // Retorna um array vazio como fallback
            }
            return prevNodes.map(node =>
                node.id === nodeId ? { ...node, data: { ...node.data, ...newData } } : node
            );
        });
    }, [setNodes]);

    const createNode = useCallback((type, initialData) => {
        const nodeId = `${type}-${idCounter}`;
        setIdCounter(idCounter + 1);

        const updateFunctions = useMemo(() => {
            return {
                message: {
                    updateContent: useCallback((content) => updateNode(nodeId, { content }), [nodeId, updateNode])
                },
                question: {
                    updateQuestion: useCallback((question) => updateNode(nodeId, { question }), [nodeId, updateNode]),
                    addOption: useCallback(() => {
                        const currentOptions = initialData.options || [];
                        const newOption = `Op√ß√£o ${currentOptions.length + 1}`;
                        updateNode(nodeId, { options: [...currentOptions, newOption] });
                    }, [initialData, nodeId, updateNode]),
                    updateOption: useCallback((index, value) => {
                        const newOptions = [...(initialData.options || [])];
                        newOptions[index] = value;
                        updateNode(nodeId, { options: newOptions });
                    }, [initialData, nodeId, updateNode])
                },
                media: {
                    handleFileUpload: useCallback((file) => updateNode(nodeId, { file }), [nodeId, updateNode]),
                    updateUrl: useCallback((url) => updateNode(nodeId, { url }), [nodeId, updateNode])
                },
                condition: {
                    updateConditionType: useCallback((conditionType) => updateNode(nodeId, { conditionType }), [nodeId, updateNode]),
                    updateField: useCallback((field) => updateNode(nodeId, { field }), [nodeId, updateNode]),
                    updateComparison: useCallback((comparison) => updateNode(nodeId, { comparison }), [nodeId, updateNode])
                },
                interval: {
                    updateDuration: useCallback((duration) => updateNode(nodeId, { duration }), [nodeId, updateNode])
                },
                chatgpt: {
                    updateApiKey: useCallback((apiKey) => updateNode(nodeId, { apiKey }), [nodeId, updateNode]),
                    updateInstructions: useCallback((instructions) => updateNode(nodeId, { instructions }), [nodeId, updateNode])
                },
                api: {
                    updateMethod: useCallback((method) => updateNode(nodeId, { method }), [nodeId, updateNode]),
                    updateUrl: useCallback((url) => updateNode(nodeId, { url }), [nodeId, updateNode]),
                    updateHeaders: useCallback((headers) => updateNode(nodeId, { headers }), [nodeId, updateNode]),
                    updateBody: useCallback((body) => updateNode(nodeId, { body }), [nodeId, updateNode])
                },
                updateCRM: {
                    updateEntity: useCallback((entity) => updateNode(nodeId, { entity }), [nodeId, updateNode]),
                    updateField: useCallback((field) => updateNode(nodeId, { field }), [nodeId, updateNode]),
                    updateValue: useCallback((value) => updateNode(nodeId, { value }), [nodeId, updateNode])
                },
                createRecord: {
                    updateEntity: useCallback((entity) => updateNode(nodeId, { entity }), [nodeId, updateNode]),
                    updateStage: useCallback((stage) => updateNode(nodeId, { stage }), [nodeId, updateNode]),
                    updateFields: useCallback((fields) => updateNode(nodeId, { fields }), [nodeId, updateNode])
                }
            };
        }, [initialData, nodeId, updateNode]);

        return {
            id: nodeId,
            type,
            position: { x: Math.random() * 400, y: Math.random() * 400 },
            draggable: true,
            data: {
                ...initialData,
                ...updateFunctions
            }
        };
    }, [idCounter, updateNode]);

    const addNode = useCallback((type) => {
        const nodeConfig = {
            message: { content: '' },
            question: { question: '', options: ['Sim', 'N√£o'] },
            media: { url: '', file: null },
            condition: { conditionType: 'cadastro', field: '', comparison: '' },
            interval: { duration: 300 },
            chatgpt: { apiKey: '', instructions: '' },
            api: { method: 'GET', url: '', headers: '', body: '' },
            updateCRM: { entity: 'lead', field: '', value: '' },
            createRecord: { entity: 'lead', stage: 'new', fields: '{}' }
        };
        setNodes(prevNodes => [...(prevNodes || []), createNode(type, nodeConfig[type])]);
    }, [createNode, setNodes]);

    return (
        <div style={{ height: '100vh', display: 'flex' }}>
            <div style={{
                width: '250px',
                padding: '20px',
                backgroundColor: '#f8f9fa',
                borderRight: '1px solid #ddd',
                overflowY: 'auto'
            }}>
                <h3>Ferramentas</h3>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    <button onClick={() => addNode('message')}>Nova Mensagem</button>
                    <button onClick={() => addNode('question')}>Nova Pergunta</button>
                    <button onClick={() => addNode('media')}>Nova M√≠dia</button>
                    <button onClick={() => addNode('condition')}>Nova Condi√ß√£o</button>
                    <button onClick={() => addNode('interval')}>Novo Intervalo</button>
                    <button onClick={() => addNode('chatgpt')}>Chat GPT</button>
                    <button onClick={() => addNode('api')}>API Externa</button>
                    <button onClick={() => addNode('updateCRM')}>Atualizar CRM</button>
                    <button onClick={() => addNode('createRecord')}>Criar Cadastro</button>
                </div>
            </div>

            {/* Container para o React Flow */}
            <div style={flowContainerStyles}>
                <ReactFlow
                    // key={nodes.length} // Voc√™ pode tentar descomentar isso se ainda tiver problemas de renderiza√ß√£o
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    nodeTypes={{
                        message: MessageNode,
                        question: QuestionNode,
                        media: MediaNode,
                        condition: ConditionNode,
                        interval: IntervalNode,
                        chatgpt: ChatGPTNode,
                        api: APINode,
                        updateCRM: UpdateCRMNode,
                        createRecord: CreateRecordNode
                    }}
                    fitView
                >
                    <MiniMap />
                    <Controls />
                    <Background />
                </ReactFlow>
            </div>
        </div>
    );
};

export default FlowEditor;
