// src/services.js
import axios from "axios";

const BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:5000";

// Função para iniciar o chat
export const startChat = async (chatId) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/start_chat`, { chat_id: chatId });
    return response.data;
  } catch (error) {
    console.error("Erro ao iniciar o chat", error);
    throw new Error("Não foi possível iniciar o chat. Tente novamente mais tarde.");
  }
};

// Função para enviar mensagem
export const sendMessage = async (message) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/send_message`, { message });
    return response.data;
  } catch (error) {
    console.error("Erro ao enviar mensagem", error);
    throw new Error("Não foi possível enviar a mensagem.");
  }
};

// Função para transferir chat
export const transferChat = async (target) => {
  try {
    const response = await axios.post(`${BASE_URL}/api/transfer_chat`, { target });
    return response.data;
  } catch (error) {
    console.error("Erro ao transferir chat", error);
    throw new Error("Não foi possível transferir a conversa.");
  }
};

