// src/StartChat.js
import React from "react";
import { startChat } from "./services";

const StartChat = ({ onStart }) => {
  const handleStartChat = async () => {
    try {
      const chatId = "chat-" + Math.random().toString(36).substr(2, 9); // Gera um ID único
      const response = await startChat(chatId);
      console.log(response.message);
      onStart(); // Chama a função para alternar para o editor de fluxo
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Comece o seu chat de atendimento!</h1>
      <p>Clique abaixo para começar a editar o seu fluxo</p>
      <button onClick={handleStartChat} style={styles.button}>
        Iniciar Chat
      </button>
    </div>
  );
};

const styles = {
  container: {
    textAlign: "center",
    padding: "20px",
    backgroundColor: "#f9f9f9",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    maxWidth: "400px",
    margin: "50px auto",
  },
  button: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  },
};

export default StartChat;
