// src/App.js
import React, { useState, useEffect } from "react";
import ReactFlow, { addEdge, MiniMap, Controls } from "react-flow-renderer";
import { saveFlow } from "./services"; // Função para enviar o fluxo para o backend

const nodeTypes = {
  message: ({ data }) => (
    <div style={{ padding: 10, backgroundColor: "#f9f9f9", borderRadius: 5 }}>
      <strong>{data.label}</strong>
    </div>
  ),
};

function App() {
  const [elements, setElements] = useState([]);

  // Função para adicionar um nó de tipo mensagem
  const addNode = () => {
    const newNode = {
      id: `node-${elements.length + 1}`,
      type: "message",  // Tipo do nó, que vai renderizar uma mensagem
      data: { label: `Mensagem ${elements.length + 1}` },
      position: { x: 100, y: 100 + elements.length * 150 },  // Ajuste a posição
      style: { border: "1px solid #ddd", padding: "10px" },
    };
    setElements((els) => [...els, newNode]);
  };

  // Função para salvar o fluxo no backend
  const saveChatFlow = async () => {
    const flowData = {
      elements,
    };
    await saveFlow(flowData);
    alert("Fluxo salvo com sucesso!");
  };

  const onConnect = (params) => setElements((els) => addEdge(params, els));

  useEffect(() => {
    // Adicionando elementos iniciais para testar
    const initialElements = [
      { id: '1', type: 'message', data: { label: 'Escolha o setor' }, position: { x: 100, y: 100 } },
      { id: '2', type: 'message', data: { label: 'Mensagem de resposta' }, position: { x: 300, y: 100 } },
      { id: 'e1-2', source: '1', target: '2', animated: true }  // Conexão entre os nós
    ];
    setElements(initialElements);
  }, []);

  return (
    <div style={{ height: "100vh" }}>
      <div style={{ position: "absolute", top: 20, left: 20 }}>
        <button onClick={addNode}>Adicionar Mensagem</button>
        <button onClick={saveChatFlow}>Salvar Fluxo</button>
      </div>
      <ReactFlow 
        elements={elements} 
        onConnect={onConnect} 
        style={{ height: "100%" }} 
        nodeTypes={nodeTypes}  // Adicionando tipos de nós
      >
        <MiniMap />
        <Controls />
      </ReactFlow>
    </div>
  );
}

export default App;
