// src/FlowEditor.js
import React, { useState } from "react";
import ReactFlow, { addEdge, Handle, MiniMap, Controls } from "react-flow-renderer";

const FlowEditor = ({ elements, setElements }) => {
  const onConnect = (params) => setElements((els) => addEdge(params, els));

  const addMessageNode = () => {
    const newNode = {
      id: `message-${elements.length + 1}`,
      type: "default",
      data: { label: "Mensagem de resposta" },
      position: { x: 100, y: 100 + elements.length * 100 },
    };
    setElements((els) => [...els, newNode]);
  };

  return (
    <div>
      <div style={{ position: "absolute", top: 20, left: 20 }}>
        <button onClick={addMessageNode}>Adicionar Mensagem</button>
        <button onClick={saveFlow}>Salvar Fluxo</button>
      </div>
      <ReactFlow elements={elements} onConnect={onConnect} style={{ height: "100%" }}>
        <MiniMap />
        <Controls />
      </ReactFlow>
    </div>
  );
};
export default FlowEditor;
