// src/FlowEditor.js
import React from "react";
import ReactFlow, { Handle, addEdge } from "react-flow-renderer";

const FlowEditor = ({ elements, setElements }) => {
  const onConnect = (params) => setElements((els) => addEdge(params, els));

  const addNode = (type) => {
    const newNode = {
      id: `node-${elements.length + 1}`,
      type: type,  // 'message' or 'question'
      data: { label: `Novo Node ${elements.length + 1}` },
      position: { x: 100, y: 100 + elements.length * 100 },
    };
    setElements((els) => [...els, newNode]);
  };

  const addQuestionNode = (options) => {
    const newQuestionNode = {
      id: `node-${elements.length + 1}`,
      type: "question", // Custom node type for questions
      data: {
        label: "Escolha uma opção",
        options: options, // Passa as opções para este nó
      },
      position: { x: 200, y: 200 + elements.length * 100 },
    };
    setElements((els) => [...els, newQuestionNode]);
  };

  return (
    <ReactFlow
      elements={elements}
      onConnect={onConnect}
      style={{ height: "100%" }}
    >
      <MiniMap />
      <Controls />

      {/* Custom buttons to trigger actions */}
      <button onClick={() => addNode("message")}>Adicionar Mensagem</button>
      <button onClick={() => addQuestionNode(["Vendas", "Cobrança", "E-commerce"])}>Adicionar Pergunta</button>
    </ReactFlow>
  );
};

export default FlowEditor;
