// src/services.js
import axios from "axios";

// Função para salvar o fluxo de chat no backend
export const saveFlow = async (flowData) => {
  try {
    const response = await axios.post("https://fe8d-2804-14c-9d83-83e7-00-10b4.ngrok-free.app /api/save_flow", flowData);
    return response.data;
  } catch (error) {
    console.error("Erro ao salvar fluxo", error);
  }
};
