// src/FlowEditor.js
import React, { useState } from "react";
import ReactFlow, { addEdge, MiniMap, Controls, Handle } from "react-flow-renderer";

// Função para salvar o fluxo
const saveFlow = async (elements) => {
  // Aqui você pode fazer a lógica para salvar no seu backend
  console.log("Fluxo salvo", elements);
  alert("Fluxo salvo com sucesso!");
};

const FlowEditor = ({ elements, setElements }) => {
  // Função para adicionar um nó de tipo mensagem
  const addMessageNode = () => {
    const newNode = {
      id: `node-${elements.length + 1}`,
      type: "message", // Tipo de nó que vai renderizar uma mensagem
      data: { label: `Mensagem ${elements.length + 1}` }, // Adicionando label
      position: { x: 100, y: 100 + elements.length * 100 }, // Ajuste a posição
      style: { border: "1px solid #ddd", padding: "10px" },
    };

    // Atualizando o estado com o novo nó
    setElements((els) => [...els, newNode]);
  };

  // Função para salvar o fluxo
  const handleSaveFlow = () => {
    saveFlow(elements); // Chama a função de salvar
  };

  const onConnect = (params) => setElements((els) => addEdge(params, els)); // Conectar nós

  return (
    <div style={{ height: "100vh" }}>
      <div style={{ position: "absolute", top: 20, left: 20 }}>
        <button onClick={addMessageNode}>Adicionar Mensagem</button>
        <button onClick={handleSaveFlow}>Salvar Fluxo</button>
      </div>
      <ReactFlow
        elements={elements}
        onConnect={onConnect}
        style={{ height: "100%" }}
        nodeTypes={{
          message: ({ data }) => (
            <div style={{ padding: 10, backgroundColor: "#f9f9f9", borderRadius: 5 }}>
              <strong>{data.label}</strong>
            </div>
          ),
        }}
      >
        <MiniMap />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default FlowEditor;
