// Defina nodeTypes fora do componente FlowEditor
const nodeTypes = {
    message: MessageNode,
    question: QuestionNode,
    media: MediaNode,
    condition: ConditionNode,
    interval: IntervalNode,
    chatgpt: ChatGPTNode,
    api: APINode,
    updateCRM: UpdateCRMNode,
    createRecord: CreateRecordNode
};

const FlowEditor = () => {
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [idCounter, setIdCounter] = useState(0);

    const onConnect = useCallback(
        (params) => setEdges((eds) => addEdge(params, eds)),
        [setEdges]
    );

    const isValidConnection = useCallback((connection) => {
        const sourceNode = nodes.find((node) => node.id === connection.source);
        const targetNode = nodes.find((node) => node.id === connection.target);

        // Permite múltiplas conexões para perguntas e condições
        if (sourceNode.type === 'question' || sourceNode.type === 'condition') {
            return true;
        }

        // Verifica se o nó de origem já tem uma conexão
        const hasExistingConnection = edges.some(
            (edge) => edge.source === connection.source
        );

        return !hasExistingConnection;
    }, [nodes, edges]);

    const deleteNode = useCallback((nodeId) => {
        setNodes((nds) => nds.filter((node) => node.id !== nodeId));
        setEdges((eds) => eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId));
    }, [setNodes, setEdges]);

    const updateNode = useCallback((nodeId, newData) => {
        setNodes((prevNodes) =>
            prevNodes.map((node) =>
                node.id === nodeId ? { ...node, data: { ...node.data, ...newData } } : node
            )
        );
    }, [setNodes]);

    const createNode = useCallback((type, initialData) => {
        const nodeId = `${type}-${idCounter}`;
        setIdCounter((prev) => prev + 1);

        const updateFunctions = {
            message: {
                updateContent: (content) => updateNode(nodeId, { content }),
                onDelete: deleteNode,
            },
            question: {
                updateQuestion: (question) => updateNode(nodeId, { question }),
                addOption: () => {
                    const currentOptions = initialData.options || [];
                    const newOption = `Opção ${currentOptions.length + 1}`;
                    updateNode(nodeId, { options: [...currentOptions, newOption] });
                },
                updateOption: (index, value) => {
                    const newOptions = [...(initialData.options || [])];
                    newOptions[index] = value;
                    updateNode(nodeId, { options: newOptions });
                },
                onDelete: deleteNode,
            },
            media: {
                handleFileUpload: (file) => updateNode(nodeId, { file }),
                updateUrl: (url) => updateNode(nodeId, { url }),
                onDelete: deleteNode,
            },
            condition: {
                updateConditionType: (conditionType) => updateNode(nodeId, { conditionType }),
                updateField: (field) => updateNode(nodeId, { field }),
                updateComparison: (comparison) => updateNode(nodeId, { comparison }),
                onDelete: deleteNode,
            },
            interval: {
                updateDuration: (duration) => updateNode(nodeId, { duration }),
                onDelete: deleteNode,
            },
            chatgpt: {
                updateApiKey: (apiKey) => updateNode(nodeId, { apiKey }),
                updateInstructions: (instructions) => updateNode(nodeId, { instructions }),
                onDelete: deleteNode,
            },
            api: {
                updateMethod: (method) => updateNode(nodeId, { method }),
                updateUrl: (url) => updateNode(nodeId, { url }),
                updateHeaders: (headers) => updateNode(nodeId, { headers }),
                updateBody: (body) => updateNode(nodeId, { body }),
                onDelete: deleteNode,
            },
            updateCRM: {
                updateEntity: (entity) => updateNode(nodeId, { entity }),
                updateField: (field) => updateNode(nodeId, { field }),
                updateValue: (value) => updateNode(nodeId, { value }),
                onDelete: deleteNode,
            },
            createRecord: {
                updateEntity: (entity) => updateNode(nodeId, { entity }),
                updateStage: (stage) => updateNode(nodeId, { stage }),
                updateFields: (fields) => updateNode(nodeId, { fields }),
                onDelete: deleteNode,
            },
        };

        return {
            id: nodeId,
            type,
            position: { x: Math.random() * 400, y: Math.random() * 400 },
            draggable: true,
            data: {
                ...initialData,
                ...updateFunctions[type],
            },
        };
    }, [idCounter, updateNode, deleteNode]);

    const addNode = useCallback((type) => {
        const nodeConfig = {
            message: { content: '' },
            question: { question: '', options: ['Sim', 'Não'] },
            media: { url: '', file: null },
            condition: { conditionType: 'cadastro', field: '', comparison: '' },
            interval: { duration: 300 },
            chatgpt: { apiKey: '', instructions: '' },
            api: { method: 'GET', url: '', headers: '', body: '' },
            updateCRM: { entity: 'lead', field: '', value: '' },
            createRecord: { entity: 'lead', stage: 'new', fields: '{}' },
        };
        setNodes((prevNodes) => [...prevNodes, createNode(type, nodeConfig[type])]);
    }, [createNode, setNodes]);

    return (
        <div style={{ height: '100vh', display: 'flex' }}>
            <div style={{
                width: '250px',
                padding: '20px',
                backgroundColor: '#f8f9fa',
                borderRight: '1px solid #ddd',
                overflowY: 'auto'
            }}>
                <h3>Ferramentas</h3>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    <button onClick={() => addNode('message')}>Nova Mensagem</button>
                    <button onClick={() => addNode('question')}>Nova Pergunta</button>
                    <button onClick={() => addNode('media')}>Nova Mídia</button>
                    <button onClick={() => addNode('condition')}>Nova Condição</button>
                    <button onClick={() => addNode('interval')}>Novo Intervalo</button>
                    <button onClick={() => addNode('chatgpt')}>Chat GPT</button>
                    <button onClick={() => addNode('api')}>API Externa</button>
                    <button onClick={() => addNode('updateCRM')}>Atualizar CRM</button>
                    <button onClick={() => addNode('createRecord')}>Criar Cadastro</button>
                </div>
            </div>

            {/* Container para o React Flow */}
            <div style={flowContainerStyles}>
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    isValidConnection={isValidConnection}
                    nodeTypes={nodeTypes}
                    fitView
                >
                    <MiniMap />
                    <Controls />
                    <Background />
                </ReactFlow>
            </div>
        </div>
    );
};

export default FlowEditor;
