import React, { useState, useCallback } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    Handle,
    useNodesState,
    useEdgesState,
    addEdge
} from 'react-flow-renderer';

// Estilos atualizados
const flowContainerStyles = {
    width: '100%',
    height: '100vh',
    position: 'relative',
    overflow: 'hidden'
};

const nodeStyles = {
    base: {
        padding: '20px',
        backgroundColor: '#fff',
        borderRadius: '8px',
        border: '2px solid #2684ff',
        minWidth: '300px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        position: 'relative'
    },
    input: {
        margin: '10px 0',
        padding: '8px',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd'
    },
    section: {
        margin: '15px 0',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '6px'
    },
    select: {
        width: '100%',
        padding: '8px',
        margin: '5px 0'
    },
    deleteButton: {
        position: 'absolute',
        top: '5px',
        right: '5px',
        background: 'red',
        color: 'white',
        border: 'none',
        borderRadius: '50%',
        width: '20px',
        height: '20px',
        cursor: 'pointer'
    }
};

// Componentes dos N√≥s com React.memo para otimiza√ß√£o
const MessageNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>X</button>
        <Handle type="target" position="top" />
        <h4>üí¨ Mensagem</h4>
        <textarea
            style={{ ...nodeStyles.input, height: '100px' }}
            value={data.content}
            onChange={(e) => data.updateContent(e.target.value)}
            placeholder="Escreva sua mensagem..."
        />
        <Handle type="source" position="bottom" />
    </div>
));

const QuestionNode = React.memo(({ id, data }) => (
    <div style={nodeStyles.base}>
        <button style={nodeStyles.deleteButton} onClick={() => data.onDelete(id)}>X</button>
        <Handle type="target" position="top" />
        <h4>‚ùì Pergunta</h4>
        <input
            type="text"
            style={nodeStyles.input}
            value={data.question}
            onChange={(e) => data.updateQuestion(e.target.value)}
            placeholder="Digite a pergunta"
        />
        <div style={nodeStyles.section}>
            <h5>Op√ß√µes de Resposta:</h5>
            {data.options.map((opt, index) => (
                <div key={index} style={{ margin: '5px 0' }}>
                    <input
                        type="text"
                        value={opt}
                        style={{ ...nodeStyles.input, width: '80%' }}
                        onChange={(e) => data.updateOption(index, e.target.value)}
                    />
                    <Handle
                        type="source"
                        position="bottom"
                        id={`option-${index}`}
                        style={{ left: '85%' }}
                    />
                </div>
            ))}
            <button onClick={data.addOption}>+ Adicionar Op√ß√£o</button>
        </div>
    </div>
));

// ... (Outros componentes de n√≥ com o bot√£o de deletar)

// Defina nodeTypes fora do componente FlowEditor
const nodeTypes = {
    message: MessageNode,
    question: QuestionNode,
    // ... (Outros tipos de n√≥)
};

const FlowEditor = () => {
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const [idCounter, setIdCounter] = useState(0);

    const onConnect = useCallback(
        (params) => setEdges((eds) => addEdge(params, eds)),
        [setEdges]
    );

    const isValidConnection = useCallback((connection) => {
        const sourceNode = nodes.find((node) => node.id === connection.source);
        const targetNode = nodes.find((node) => node.id === connection.target);

        // Permite m√∫ltiplas conex√µes para perguntas e condi√ß√µes
        if (sourceNode.type === 'question' || sourceNode.type === 'condition') {
            return true;
        }

        // Verifica se o n√≥ de origem j√° tem uma conex√£o
        const hasExistingConnection = edges.some(
            (edge) => edge.source === connection.source
        );

        return !hasExistingConnection;
    }, [nodes, edges]);

    const deleteNode = useCallback((nodeId) => {
        setNodes((nds) => nds.filter((node) => node.id !== nodeId));
        setEdges((eds) => eds.filter((edge) => edge.source !== nodeId && edge.target !== nodeId));
    }, [setNodes, setEdges]);

    const updateNode = useCallback((nodeId, newData) => {
        setNodes((prevNodes) =>
            prevNodes.map((node) =>
                node.id === nodeId ? { ...node, data: { ...node.data, ...newData } } : node
            )
        );
    }, [setNodes]);

    const createNode = useCallback((type, initialData) => {
        const nodeId = `${type}-${idCounter}`;
        setIdCounter((prev) => prev + 1);

        const updateFunctions = {
            message: {
                updateContent: (content) => updateNode(nodeId, { content }),
                onDelete: deleteNode,
            },
            question: {
                updateQuestion: (question) => updateNode(nodeId, { question }),
                addOption: () => {
                    const currentOptions = initialData.options || [];
                    const newOption = `Op√ß√£o ${currentOptions.length + 1}`;
                    updateNode(nodeId, { options: [...currentOptions, newOption] });
                },
                updateOption: (index, value) => {
                    const newOptions = [...(initialData.options || [])];
                    newOptions[index] = value;
                    updateNode(nodeId, { options: newOptions });
                },
                onDelete: deleteNode,
            },
            // ... (Outros tipos de n√≥ com onDelete)
        };

        return {
            id: nodeId,
            type,
            position: { x: Math.random() * 400, y: Math.random() * 400 },
            draggable: true,
            data: {
                ...initialData,
                ...updateFunctions[type],
            },
        };
    }, [idCounter, updateNode, deleteNode]);

    const addNode = useCallback((type) => {
        const nodeConfig = {
            message: { content: '' },
            question: { question: '', options: ['Sim', 'N√£o'] },
            // ... (Outros tipos de n√≥)
        };
        setNodes((prevNodes) => [...prevNodes, createNode(type, nodeConfig[type])]);
    }, [createNode, setNodes]);

    return (
        <div style={{ height: '100vh', display: 'flex' }}>
            <div style={{
                width: '250px',
                padding: '20px',
                backgroundColor: '#f8f9fa',
                borderRight: '1px solid #ddd',
                overflowY: 'auto'
            }}>
                <h3>Ferramentas</h3>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    <button onClick={() => addNode('message')}>Nova Mensagem</button>
                    <button onClick={() => addNode('question')}>Nova Pergunta</button>
                    {/* ... (Outros bot√µes de adicionar n√≥) */}
                </div>
            </div>

            {/* Container para o React Flow */}
            <div style={flowContainerStyles}>
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    isValidConnection={isValidConnection}
                    nodeTypes={nodeTypes}
                    fitView
                >
                    <MiniMap />
                    <Controls />
                    <Background />
                </ReactFlow>
            </div>
        </div>
    );
};

export default FlowEditor;
